//                 {task.description}
//             </PTag>
//             <PTag size='14'>
//                 {task.status}
//             </PTag>
//             <div 
//                 onClick={() => {
//                     deleteCurrentProjectTask(task.id || '')
//                 }} 
//                 className={styles.x}
//             ></div>
//         </div>
//     </CSSTransition>
// )
interface IProp {
    task: ITask
    deleteCurrentProjectTask: (id: string) => void
}
const TaskBlock = ({...props}: IProp) => {

    return (
        <CSSTransition 
            key={task.id} 
            timeout={500} 
            classNames={styles.item}
        >
            <div className={styles.task}>
                <HTag htag='h3'>
                    {task.name}
                </HTag>
                <PTag size='18'>
                    {task.description}
                </PTag>
                <PTag size='14'>
                    {task.status}
                </PTag>
                <div 
                    onClick={() => {
                        deleteCurrentProjectTask(task.id || '')
                    }} 
                    className={styles.x}
                ></div>
            </div>
        </CSSTransition>

    )
}

export default Todos;
