import { BrowserRouter as Router, Routes, Route, useNavigate} from "react-router-dom";
import AuthPage from "./AuthPage/AuthPage";
import MainPage from "./MainPage/MainPage";
import { withLayout } from "./Layout/Layout";
import { useTypedSelector } from "../store/selectorTypedHook";
import { useEffect, useState } from "react";
import { check } from "./AuthPage/modules/services/userAPI";
import { useDispatch } from "react-redux";
import { AppDispatch } from "../store/store";
import { fetchedUser, fetchingUser, fetchUser } from "./AuthPage/modules/services/userSlice";

const RouterComponent = ():JSX.Element => {

    const auth = useTypedSelector(state => state.user.userLoadingStatus);

    const [loading, setLoading] = useState(true);

    const dispatch = useDispatch<AppDispatch>();

    useEffect(() => {
        check()
            .then((data) => {
                dispatch(fetchingUser());
                dispatch(fetchUser(data));
            })
            .catch(() => {
                dispatch(fetchedUser());
            })
    }, [])

    return (
        <Router>
            <Routes>
                <Route path="/" element={<MainPage/>}/>
                <Route path="/auth" element={<AuthPage/>}/>
            </Routes>
        </Router>
    );
};

export default withLayout(RouterComponent);
