import jwt_decode from "jwt-decode";
import { $authHost, $host} from './http.user';



export const registration = async (email: string, password: string): Promise<string> => {
    const { data } = await $host.post('user/registration', {email, password, role:'user'});    
    const token = data.token;
    return token;
}

export const login = async (email: string, password: string) => {
    const response = $host.post('user/login', {email, password});
    return response;
}

