import React, { useEffect, useState } from 'react';
import HTag from '../../../../../ui/Htag/HTag';
import PTag from '../../../../../ui/PTag/PTag';
import styles from './Post.module.css';
import { useParams } from 'react-router-dom';
import { getOnePost } from '../../services/postAPI';
import { IPost } from '../../../../PostsPage/modules/interfaces/Posts.interface';

const Post = () => {

    const [post, setPost] = useState<IPost>();

    const { postId } = useParams();


    useEffect(() => {
        try {
            getPost();
        } catch (error) {
            console.log(error);
        }
    }, [])

    const getPost = async () => {
        if (postId !== undefined) {
            await getOnePost(parseInt(postId));
        }
    }

    return (
        <>
            {
                post ? 
                <div className={styles.post}>
                    <HTag className={styles.heading} htag="h2">{post.name}</HTag>
                    <PTag className={styles.description} size="16">{post.description}</PTag>
                    <PTag className={styles.date} size="14">{new Date(post.date).toLocaleDateString()}</PTag>
                </div>
                :
                null
            }
        </>
    );
};

export default Post;
